<script>
(function() {
var runExpansion = true;
if (runExpansion) {
var regionZip = bmo.core.getZipCode(),
    region,
    expansionOffer = {
        img: "https://www.bmoharris.com/img/large/summer-cust-growth-banner-expansion-desktop.jpg",
        href: "https://www.bmoharris.com/main/personal/combo-exp/?COID=BMOP-01601D-WEB500E&icid=ba-FEAT1813CHK4-JMBMO22",
        copy: "Our biggest offer ever has arrived. Get up to a $500 bonus when you open a new checking account and a money market account with qualifying activities*.",
        mobileCopy: " < span class = 'bold-text' > Get up to a $500 bonus < /span> when you open a new checking account and a money market account with qualifying activities < sup > * < /sup>."
    },
    oofOffer = {
        img: "https://www.bmoharris.com/img/large/mm-banner-desktop.jpg",
        imgMob: "https://www.bmoharris.com/img/small/mm-banner-mobile.jpg",
        copy: "Your savings expect more. Open a Platinum Money Market account with BMO Harris. Start earning more today.",
        href: "https://www.bmoharris.com/main/personal/savings-and-cds/money-markets/?icid=ba-FEAT1783SVG4-JMBMOH15",
        mobileCopy: "Your savings  < span class = 'bold-text' > expect more < /span>. Open a Platinum Money Market account with BMO Harris. Start earning more today."
    };
if (!regionZip && window.geoInfo) {
    regionZip = geoInfo.zipcode;
}

function showExpansionContent() {
    var viewportWidth = bmo.core.getViewportWidth();
    var $banner = $('[data-expansion-offer]');
    $banner.find('a').attr('href', expansionOffer.href);
    $banner.find('img').attr('data-src-lrg', expansionOffer.img);
    $banner.find('img').attr('alt', expansionOffer.copy);
    $banner.find('img').attr('title', expansionOffer.copy);
    $banner.find('.copy p').html(expansionOffer.mobileCopy);
    if (viewportWidth > 767) {
        $banner.find('img').attr('src', expansionOffer.img);
    }
}

function showOofContent() {
    var viewportWidth = bmo.core.getViewportWidth();
    var $banner = $('[data-expansion-offer]');
    var $bannerMobilePanel = $banner.find('.desktop-hide');
    var $nextMobilePanel = $banner.next().find('.desktop-hide').clone();
    $bannerMobilePanel.replaceWith($nextMobilePanel);
    $banner.find('img').attr('data-src-lrg', oofOffer.img);
    $banner.find('a').attr('href', oofOffer.href);
    $banner.find('img').attr('data-src-sml', oofOffer.imgMob);
    $banner.find('img').attr('alt', oofOffer.copy);
    $banner.find('img').attr('title', oofOffer.copy);
    $banner.find('.copy p').html(oofOffer.mobileCopy);
    if (viewportWidth > 767) {
        $banner.find('img').attr('src', oofOffer.img);
    } else {
        $banner.find('img').attr('src', oofOffer.imgMob);
    }
}
bmo.core.getRegion(regionZip).done(function(data) {
    if (data.region === 'NOTFOUND') {
        showOofContent();
    } else {
        region = parseInt(data.region, 10);
        // the following list of numbers can be changed
        var WisRegions = [4, 5, 6, 20];
        var IllRegions = [1, 2];
        var ExpRegions = [3, 7, 8, 9, 10, 11, 12, 13, 16];
        var OutOfFootprint = [14, 15, 17, 18, 19];
        if (region !== undefined && region !== 'NOTFOUND' && regionZip !== 00000) {
            if (WisRegions.indexOf(region) > -1) {
                /*comment the following line if you need Wisconsin experience hide */
                // showCoreContent();
            } else if (IllRegions.indexOf(region) > -1) {
                /*comment the following line if you need Illinois experience hide */
                // showCoreContent();
            } else if (OutOfFootprint.indexOf(region) > -1) {
                showOofContent();
            } else if (ExpRegions.indexOf(region) > -1) {
                showExpansionContent();
            } else {
                showOofContent();
            }
        }
    }
});
}
